clear all;                 % clears plotclose all;                 % closes plotx = [0:1];                 % establish range for xplot(x, hubbard(x, 0.25)); % plot the hubbard function w/ respect to xhold on;                   % tells MatLab not to delete the above plotplot(x, hubbard(x, 0.75)); % plot the hubbard function w/ respect to xhold on;                   % tells MatLab not to delete the above plotplot(x, exp(x), 'r');      % plots the exp function w/ respect to xlegend('hubbard t = 0.25', 'hubbard t = 0.75', 'exponential');  % creates a legend for the functions graphedgrid on                    % turns on the background grid on the graph